package fr.declaration.variable;

public class DeclarationApp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte myByteVariable = 1;
		short myShortVariable = 2;
		int myIntVariable = 3;
		long myLongVariable = 1L;
		float myFloatVariable = 0.3f;
		double myDoubleVariable = 0.4;
		char myCharvariable = 'f';
		boolean myBooleanVariable = true;
		String myStringVariable = "ProjectDivinity";
		
		System.out.println(myStringVariable);
		
		String randomString = "Voici le résultat d’un calcul : \n1+5=6";
		
		System.out.println(randomString);
		
		int a = 047;
		System.out.println(a);	
	}

}
